\copy (select distinct ti.title_instance_id,ta.title_artifact_id, m.title, title_artifact_format_id from title_instance ti join title_artifact ta using (title_instance_id)  join book_metadata m using (title_metadata_id) join book_metadata_to_category using (title_metadata_id) join category c using (category_id) join title_to_site using (title_id) where title_artifact_format_id in (11, 37, 8, 3) and title_instance_status_id=5 and parent_title_artifact_id is null and site_id=1 and c.name like 'Math%') TO 'source_artifact.csv' CSV;